mapper.update:
UPDATE BOARD_TB SET
READ_COUNT = READ_COUNT + 1
WHERE BNO = #{bno}

UPDATE BOARD_TB SET
RECOMMEND = RECOMMEND + 1
WHERE BNO = #{bno}

UPDATE BOARD_TB SET
RECOMMEND = RECOMMEND - 1
WHERE BNO = #{bno}

mapper.select:
SELECT COUNT(*)
FROM RECOMMEND_TB
WHERE USERID = #{userid} AND BNO = #{bno}

SELECT COUNT(*)
FROM RECOMMEND_TB
WHERE BNO = #{bno}

SELECT BNO, TITLE, WRITER, WRITE_DATE, READ_COUNT, CATEGORY, RECOMMEND, SOLDOUT
FROM BOARD_TB


		WHERE category = #{category}

			and title like concat('%', #{word}, '%')


			and writer like concat('%', #{word}, '%')



		WHERE category = #{category}





			WHERE title like concat('%', #{word}, '%')


			WHERE writer like concat('%', #{word}, '%')



ORDER BY ${sort} DESC
LIMIT #{start}, #{spp}

SELECT BNO, TITLE, WRITER, WRITE_DATE, READ_COUNT, CONTENT, CATEGORY, RECOMMEND, SOLDOUT
FROM BOARD_TB
WHERE BNO = #{bno}

mapper.update:
UPDATE BOARD_TB
SET TITLE = #{title}, CONTENT = #{content}, category = #{category}, soldout = #{soldout}
WHERE BNO = #{bno}
