mapper.select:
select username, userid, email
from member_tb
where userid = #{userid} and userpwd = #{userpwd}

select username, userid, email, joindate
from member_tb
where userid = #{userid}

select token
from member_tb
where userid = #{userid}

mapper.update:
update member_tb
set token = #{token}
where userid = #{userid}

update member_tb
set token = #{token, jdbcType=VARCHAR}
where userid = #{userid}
